/**
 * Logs the given argument in the browser's console highlighting it with a green background.
 * @method   happy
 * @global
 * @example
 * ``` javascript
 * bbn.fn.happy('I want to log the success of my function');
 * ```
 * @memberof bbn.fn
 * @param    {...any} args
 * @returns
 */
export default function happy(...args: any[]): any;
