/**
 * Returns the string passed as an argument without accents.
 *
 * @method   removeAccents
 * @global
 *
 * @example
 * ```javascript
 * //"eeou"
 * bbn.fn.removeAccents("èéòù");
 * ```
 * @memberof bbn.fn
 * @param    {String} st
 * @returns  {String}
 */
export default function removeAccents(st: string): string;
